package edu.zntu.file;import java.io.*;import java.text.SimpleDateFormat;import java.util.Scanner;public class DemoFile{	public static void main(String [] args){		Scanner consoleIn = new Scanner(System.in);		System.out.println("Hello, enter your name ");		String name = consoleIn.nextLine();		System.out.println(" Enter your academical group ");		String group = consoleIn.nextLine();		consoleIn.close();		String text = name + " " + group + "\n";		String filename = "myinfo.txt";		PrintWriter writer = null;		BufferedReader reader = null;		File file = new File(filename);		try{			writer = new PrintWriter(new BufferedWriter(new FileWriter(file)));			writer.println(text);			if(writer != null){				writer.close();			}			reader = new BufferedReader(new FileReader(file));			String line;			while((line = reader.readLine()) != null){				System.out.println(line);			}			if(reader != null){				reader.close();			}		} catch(IOException ex){			System.out.println(ex.getMessage());		}		if(file.exists()){			if(file.isFile()){				System.out.println("filename:\t " + file.getName());				System.out.println("path:\t " + file.getAbsolutePath());				System.out.println("length:\t " + file . length ());				SimpleDateFormat sdf = new SimpleDateFormat("dd.MM.yyyy HH:mm:ss") ;				System.out.println("modified:\t " + sdf.format(file.lastModified()));			}		} else			System.out.println("file " + file.getName() + " is missing ") ;	}}