package edu.zntu.file;import java.io.*;import java.text.SimpleDateFormat;import java.util.Scanner;public class DemoFile{	public static void main(String [] args){		PrintWriter writer = null;		BufferedReader reader = null;		String fileForReadingName = "fileForReading.txt";		File fileForReading = new File(fileForReadingName);		String fileForWritingName = "fileForWriting.txt";		File fileForWriting = new File(fileForWritingName);				if(!fileForReading.exists() || !fileForReading.isFile()){			System.out.println("file " + fileForReading.getName() + " is missing or incorrect");		} 				try{			reader = new BufferedReader(new FileReader(fileForReading));			writer = new PrintWriter(new BufferedWriter(new FileWriter(fileForWriting)));			String lineData;			SimpleDateFormat sdf = new SimpleDateFormat("dd.MM.yyyy HH:mm:ss");			lineData = fileForWriting.getName() + "\t" + sdf.format(fileForWriting.lastModified()) + "\tFilippenkov Kyrylo";			writer.print(lineData);			System.out.println(lineData);			while ((lineData = reader.readLine()) != null){                writer.println(lineData);				System.out.println(lineData);            }			if(reader != null){				reader.close();			}			if(writer != null){				writer.close();			}		} catch(IOException ex){			System.out.println(ex.getMessage());		}	}}